import React, { useState } from 'react'
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button'
import TextField from '@material-ui/core/TextField'
import Dialog from '@material-ui/core/Dialog'
import DialogActions from '@material-ui/core/DialogActions'
import DialogContent from '@material-ui/core/DialogContent'
import DialogContentText from '@material-ui/core/DialogContentText'
import DialogTitle from '@material-ui/core/DialogTitle'
import InputLabel from '@material-ui/core/InputLabel';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import NativeSelect from '@material-ui/core/NativeSelect';
import { Add } from '@material-ui/icons'
import { useSelector, useDispatch } from 'react-redux'

import CharacterNameAutoComplete from './CharacterNameAutoComplete'
import cast from '../reducers/cast'
import { bondCategories } from '../constants'

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
}))

export const BondSubTypeSelect = ({ category, onChange }) => {

  const characters = useSelector(store => store.cast.characters)
  const characterNames = useSelector(store => store.cast.characters).map(character => character.name)

  const classes = useStyles()
  const dispatch = useDispatch()

  if (category === undefined || category === null ) {
    return (
      <div>
      </div>
    )
  } else {
    return (
      <div>
        <FormControl className={classes.formControl}>
          <InputLabel htmlFor="character-a">Subtype</InputLabel>
          <NativeSelect
            value={category.value}
            onChange={onChange}
            inputProps={{
              name: 'category',
              id: 'category',
            }}
          >
            {bondCategories.map((category) => {
              return <option value={category.name}>{category.name}</option>
            })}
  
          </NativeSelect>
          <FormHelperText>the Bond's rough subtype</FormHelperText>
        </FormControl>
      </div>
    )
  }  
}

export default BondSubTypeSelect
