import React, { useState } from 'react'
import Button from '@material-ui/core/Button'
import TextField from '@material-ui/core/TextField'
import Dialog from '@material-ui/core/Dialog'
import DialogActions from '@material-ui/core/DialogActions'
import DialogContent from '@material-ui/core/DialogContent'
import DialogContentText from '@material-ui/core/DialogContentText'
import DialogTitle from '@material-ui/core/DialogTitle'
import { Add } from '@material-ui/icons'
import { useSelector, useDispatch } from 'react-redux'

import CharacterNameAutoComplete from './CharacterNameAutoComplete'
import cast from '../reducers/cast'

export const NewBondForm = () => {

  const characters = useSelector(store => store.cast.characters)
  const [open, setOpen] = useState(false)
  const [source, setSource] = useState("")
  const [target, setTarget] = useState("")
  const [type, setType] = useState("")
  const [section, setSection] = useState("")
  const [timeStart, setTimeStart] = useState("")
  const [timeEnd, setTimeEnd] = useState("")

  const dispatch = useDispatch()

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleSourceChange = (event) => {
    setSource(event.target.value)
  }

  const handleTargetChange = (event) => {
    setTarget(event.target.value)
  }

  const handleClose = () => {
    setOpen(false);
  };

  const handleSubmit = () => {
    setSource("")
    setTarget("")
    dispatch(cast.actions.addBond({source, target}))
    setOpen(false);
  };

  return (
    <div>
      <Button variant="outlined" color="primary" onClick={handleClickOpen}>
        <Add fontSize="small"/>
        ADD BOND
      </Button>
      <Dialog open={open} onClose={handleClose} aria-labelledby="form-dialog-title">
        <DialogTitle id="form-dialog-title">Add Bond</DialogTitle>
        <DialogContent>
          <DialogContentText>
            ???
          </DialogContentText>

          <CharacterNameAutoComplete label="who has the relation...?" value={source} onChange={handleSourceChange}/>

          <CharacterNameAutoComplete label="... with who?" value={target} onChange={handleTargetChange}/>

          {/* <TextField
            autoFocus
            margin="dense"
            id="source"
            label="source"
            fullWidth
            value={source}
            onChange={handleSourceChange}
          />
          <TextField
            margin="dense"
            id="target"
            label="short target"
            fullWidth
            value={target}
            onChange={handleTargetChange}
          /> */}


        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            CANCEL
          </Button>
          <Button variant="contained" onClick={handleSubmit} color="primary">
            <Add fontSize="small"/>
            ADD
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}

export default NewBondForm
