import React, { useState } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import * as joint from 'jointjs'

import cast from '../reducers/cast'
// import { bondCategories } from '../constants'


const DrawBonds = ({ graph }) => {
  
  const characters = useSelector(store => store.cast.characters)

  characters.forEach((character, index) => {
    let rect = new joint.shapes.standard.Rectangle();
    rect.position(150+index, 50-index);
    rect.resize(100, 40);
    rect.attr({
      body: {
        fill: 'green'
      },
      label: {
        text: character.name,
        fill: 'white'
      }
    });
    rect.addTo(graph)
  })

  console.log(graph.getElements())

  return (
    <div>
    </div>
  )
}

export default DrawBonds
