import React from 'react'
import ReactDOM from 'react-dom'
import { useSelector } from 'react-redux'
import { makeStyles } from '@material-ui/core/styles'
import Accordion from '@material-ui/core/Accordion'
import AccordionSummary from '@material-ui/core/AccordionSummary'
import AccordionDetails from '@material-ui/core/AccordionDetails'
import Typography from '@material-ui/core/Typography'
import ExpandMoreIcon from '@material-ui/icons/ExpandMore'
import Button from '@material-ui/core/Button'

import Bond from './Bond'

const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
  },
  heading: {
    fontSize: theme.typography.pxToRem(15),
    flexBasis: '66.66%',
    flexShrink: 0,
  },
  secondaryHeading: {
    fontSize: theme.typography.pxToRem(15),
    color: theme.palette.text.secondary,
  },
  accordionContent: {
    fontSize: theme.typography.pxToRem(15),
    display: 'flex',
    flexDirection: 'column'
  },
}));

export const Character = ({ character }) => {
  
  const bonds = useSelector(store => store.cast.bonds)
  const classes = useStyles();
  const [expanded, setExpanded] = React.useState(false);

  const handleChange = (panel) => (event, isExpanded) => {
    setExpanded(isExpanded ? panel : false);
  }

  console.log(bonds)

  const bondsSourceOf = bonds.map((bond) => {
    if (bond.source === character.id) {
      console.log(`inside bondsSourceOf and ${bond} seems like a fine bond`)
      return bond
    }
  })

  const bondsTargetOf = bonds.map((bond) => {
    if (bond.target === character.id) {
      console.log(`inside bondsTargetOf and ${bond} seems like a fine bond`)
      return bond
    }
  })

  console.log(bondsSourceOf)
  console.log(bondsTargetOf)

  const relevantBondCategories = [...new Set(Array.join(bondsSourceOf, bondsTargetOf).map(bond => bond.category))]
  console.log(relevantBondCategories)

  // const childrenTest = () => {
  //   return (
  //     <p>HELLO I AM CHILD</p>
  //   )
  // }

  return (
    <Accordion>
      <AccordionSummary
        expandIcon={<ExpandMoreIcon />}
        aria-label="Expand"
        aria-controls="additional-actions1-content"
        id="additional-actions1-header"
      >
        <Typography className={classes.heading}>{character.name}</Typography>
        <div className='character-header-buttons'>
          <Button 
            size="small"
            onClick={(event) => event.stopPropagation()}
            onFocus={(event) => event.stopPropagation()}
          >
            RENAME
          </Button>
          <Button 
            size="small"
            onClick={(event) => event.stopPropagation()}
            onFocus={(event) => event.stopPropagation()}
          >
            REMOVE
          </Button>
        </div>
      </AccordionSummary>
      <AccordionDetails className={classes.accordionContent}>
        
        {/* ----------- CATEGORY ----------- */}

        <Accordion>
          <AccordionSummary
            expandIcon={<ExpandMoreIcon />}
            aria-label="Expand"
            aria-controls="additional-actions1-content"
            id="additional-actions1-header"
          >
            <Typography className={classes.secondaryHeading}>Category name</Typography>
          </AccordionSummary>
          <AccordionDetails className={classes.accordionContent}>
          
            {/* ----------- BOND ----------- */}
            <Accordion>
              <AccordionSummary
                expandIcon={<ExpandMoreIcon />}
                aria-label="Expand"
                aria-controls="additional-actions1-content"
                id="additional-actions1-header"
              >
                <Typography className={classes.heading}>Bond</Typography>
              </AccordionSummary>
              <AccordionDetails className={classes.accordionContent}>
                THIS IS A BOND
              </AccordionDetails>
            </Accordion>

            {/* <Bond /> */}
          
          </AccordionDetails>
        </Accordion>

      </AccordionDetails>
    </Accordion>
  )
}

export default Character
