/* eslint-disable no-use-before-define */
import React, { useState } from 'react';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogTitle from '@material-ui/core/DialogTitle';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogActions from '@material-ui/core/DialogActions';
import Button from '@material-ui/core/Button';
import Autocomplete, { createFilterOptions } from '@material-ui/lab/Autocomplete';
import NewCharacterForm from './NewCharacterForm';
import { useSelector } from 'react-redux'

const filter = createFilterOptions();

export const CharacterNameAutoComplete = ({ label, value, onChange }) => {
  // const [value, setValue] = React.useState(null);
  
  const characters = useSelector(store => store.cast.characters)
  const [open, toggleOpen] = useState(false)

  const handleClose = () => {
    // setDialogValue({
    //   title: '',
    //   year: '',
    // });

    toggleOpen(false);
  };

  // const handleClickOpen = () => {
  //   toggleOpen(true);
  // };

  // const [dialogValue, setDialogValue] = React.useState({
  //   title: '',
  //   year: '',
  // });

  // const handleSubmit = (event) => {
  //   event.preventDefault();
  //   setValue({
  //     title: dialogValue.title,
  //     year: parseInt(dialogValue.year, 10),
  //   });

  //   handleClose();
  // };

  return (
    <>
      <Autocomplete
        value={value}
        onChange={(event, newValue) => {
          if (typeof newValue === 'string') {
            // timeout to avoid instant validation of the dialog's form.
            setTimeout(() => {
              toggleOpen(true);
              // setDialogValue({
              //   title: newValue,
              //   year: '',
              // });
            });
          } else if (newValue && newValue.inputValue) {
            toggleOpen(true);
            // setDialogValue({
            //   title: newValue.inputValue,
            //   year: '',
            // });
          } else {
            setValue(newValue);
          }
        }}
        filterOptions={(options, params) => {
          const filtered = filter(options, params);

          if (params.inputValue !== '') {
            filtered.push({
              inputValue: params.inputValue,
              title: `Add "${params.inputValue}"`,
            });
          }

          return filtered;
        }}
        id="free-solo-dialog-demo"
        options={characters}
        getOptionLabel={(option) => {
          // e.g value selected with enter, right from the input
          if (typeof option === 'string') {
            return option;
          }
          if (option.inputValue) {
            return option.inputValue;
          }
          return option.title;
        }}
        selectOnFocus
        clearOnBlur
        handleHomeEndKeys
        renderOption={(option) => option.title}
        style={{ width: 300 }}
        freeSolo
        renderInput={(params) => (
          <TextField {...params} label="Free solo dialog" variant="outlined" />
        )}
      />
      <NewCharacterForm 
        open={open} handleClose={handleClose}
      />
    </>
  )
}

export default CharacterNameAutoComplete
