import React, { useState } from 'react'
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button'
import TextField from '@material-ui/core/TextField'
import Dialog from '@material-ui/core/Dialog'
import DialogActions from '@material-ui/core/DialogActions'
import DialogContent from '@material-ui/core/DialogContent'
import DialogContentText from '@material-ui/core/DialogContentText'
import DialogTitle from '@material-ui/core/DialogTitle'
import InputLabel from '@material-ui/core/InputLabel';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import NativeSelect from '@material-ui/core/NativeSelect';
import { Add } from '@material-ui/icons'
import { useSelector, useDispatch } from 'react-redux'

import CharacterNameAutoComplete from './CharacterNameAutoComplete'
import BondSubTypeSelect from './BondSubtypeSelect'
import cast from '../reducers/cast'
import { bondCategories } from '../constants'

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
}))

export const NewBondForm = ({ open, handleClose }) => {

  const characters = useSelector(store => store.cast.characters)
  const characterNames = useSelector(store => store.cast.characters).map(character => character.name)
  // const [open, setOpen] = useState(false)
  const [source, setSource] = useState({ id:"", value:""})
  const [target, setTarget] = useState({ id:"", value:""})
  const [type, setType] = useState({ id:"", value:""})
  const [category, setCategory] = useState({ id:"", value:"", index: null})
  const [timeStart, setTimeStart] = useState({ id:"", value:""})
  const [timeEnd, setTimeEnd] = useState({ id:"", value:""})
  const [summary, setSummary] = useState({ id:"", value:""})
  const classes = useStyles()

  const dispatch = useDispatch()

  // const handleClickOpen = () => {
  //   setOpen(true);
  // };

  const handleSourceChange = (event) => {
    setSource({ id: source.id, value: event.target.value })
  }

  const handleTargetChange = (event) => {
    setTarget({ id: target.id, value: event.target.value })
  }

  const handleCategoryChange = (event) => {
    console.log("---------- about to set this mofucking category!! -----------")
    console.log("event.target.index:")
    console.log(event.target.index)
    setCategory({ id: category.id, value: event.target.value, index: event.target.index })
  }

  const handleTypeChange = (event) => {
    setType({ id: type.id, value: event.target.value })
  }

  const handleSummaryChange = (event) => {
    setSummary({ id: summary.id, value: event.target.value })
  }

  // const handleClose = () => {
  //   setOpen(false);
  // };

  const handleSubmit = () => {
    setSource("")
    setTarget("")
    dispatch(cast.actions.addBond({source, target}))
    handleClose()
  };

  console.log("new bond form mounted!")
  console.log("category index is:")
  console.log(bondCategories)
  console.log(category.index)
  return (
    <div>
      <Dialog open={open} onClose={handleClose} aria-labelledby="form-dialog-title">
        <DialogTitle id="form-dialog-title">Add Bond</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Add a new Bond between two registered Characters
          </DialogContentText>

            <FormControl className={classes.formControl}>
              <InputLabel htmlFor="character-a">Category</InputLabel>
              <NativeSelect
                value={category.value}
                onChange={handleCategoryChange}
                inputProps={{
                  name: 'category',
                  id: 'category',
                }}
              >
                <option aria-label="None" value="" />
                {bondCategories.map((category) => {
                  return <option value={category.name}>{category.name}</option>
                })}
                
              </NativeSelect>
              <FormHelperText>the Bond's rough category</FormHelperText>
            </FormControl>

            <FormControl className={classes.formControl}>
              <InputLabel htmlFor="character-a">Character A</InputLabel>
              <NativeSelect
                value={source.value}
                onChange={handleSourceChange}
                inputProps={{
                  name: 'character-a',
                  id: 'character-a',
                }}
              >
                <option aria-label="None" value="" />
                {characterNames.map((name) => {
                  return <option value={name}>{name}</option>
                })}
                
              </NativeSelect>
              <FormHelperText>the Bond "owner"</FormHelperText>
            </FormControl>
            
            <BondSubTypeSelect category={category.name} onChange={handleTypeChange}/>
            {/* <FormControl className={classes.formControl}>
              <InputLabel htmlFor="character-b">Bond type</InputLabel>
              <NativeSelect
                value={target.value}
                onChange={handleTargetChange}
                inputProps={{
                  name: 'character-b',
                  id: 'character-b',
                }}
              >
                {category.index !== undefined && category.index !== null 
                ? <option aria-label="None" value="">please first select a Category</option> 
                : bondCategories[category.index].subtypes.map((subtype) => {
                  return <option value={subtype}>{subtype}</option>
                }
                )}
                
              </NativeSelect>
              <FormHelperText>a rough outline</FormHelperText>
            </FormControl> */}
            
            <FormControl className={classes.formControl}>
              <InputLabel htmlFor="character-b">Character B</InputLabel>
              <NativeSelect
                value={target.value}
                onChange={handleTargetChange}
                inputProps={{
                  name: 'character-b',
                  id: 'character-b',
                }}
              >
                <option aria-label="None" value="" />
                {characterNames.map((name) => {
                  return <option value={name}>{name}</option>
                })}
                
              </NativeSelect>
              <FormHelperText>the Bond "target"</FormHelperText>
            </FormControl>
          {/* <CharacterNameAutoComplete label="who has the relation...?" value={source} onChange={handleSourceChange}/>

          <CharacterNameAutoComplete label="... with who?" value={target} onChange={handleTargetChange}/> */}

          {/* <TextField
            autoFocus
            margin="dense"
            id="source"
            label="source"
            fullWidth
            value={source}
            onChange={handleSourceChange}
          />
          <TextField
            margin="dense"
            id="target"
            label="short target"
            fullWidth
            value={target}
            onChange={handleTargetChange}
          /> */}


        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            CANCEL
          </Button>
          <Button variant="contained" onClick={handleSubmit} color="primary">
            <Add fontSize="small"/>
            ADD
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}

export default NewBondForm
