import React, { useState } from 'react'
import Button from '@material-ui/core/Button'
import TextField from '@material-ui/core/TextField'
import Dialog from '@material-ui/core/Dialog'
import DialogActions from '@material-ui/core/DialogActions'
import DialogContent from '@material-ui/core/DialogContent'
import DialogContentText from '@material-ui/core/DialogContentText'
import DialogTitle from '@material-ui/core/DialogTitle'
import InputLabel from '@material-ui/core/InputLabel';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import NativeSelect from '@material-ui/core/NativeSelect';
import { Add } from '@material-ui/icons'
import { useSelector, useDispatch } from 'react-redux'

import CharacterNameAutoComplete from './CharacterNameAutoComplete'
import cast from '../reducers/cast'

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
}))

export const NewBondForm = ({ open, handleClose }) => {

  const characters = useSelector(store => store.cast.characters)
  const characterNames = useSelector(store => store.cast.characters).map(character => character.name)
  // const [open, setOpen] = useState(false)
  const [source, setSource] = useState({ id:"", value:""})
  const [target, setTarget] = useState({ id:"", value:""})
  const [type, setType] = useState("")
  const [section, setSection] = useState("")
  const [timeStart, setTimeStart] = useState("")
  const [timeEnd, setTimeEnd] = useState("")
  const classes = useStyles()

  const dispatch = useDispatch()

  // const handleClickOpen = () => {
  //   setOpen(true);
  // };

  const handleSourceChange = (event) => {
    setSource({ id: source.id, value: event.target.value })
  }

  const handleTargetChange = (event) => {
    setTarget({ id: target.id, value: event.target.value })
  }

  // const handleClose = () => {
  //   setOpen(false);
  // };

  const handleSubmit = () => {
    setSource("")
    setTarget("")
    dispatch(cast.actions.addBond({source, target}))
    handleClose()
  };

  console.log("new bond form mounted!")

  return (
    <div>
      <Dialog open={open} onClose={handleClose} aria-labelledby="form-dialog-title">
        <DialogTitle id="form-dialog-title">Add Bond</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Add a new Bond between two registered Characters
          </DialogContentText>

            <FormControl className={classes.formControl}>
              <InputLabel htmlFor="age-native-helper">Character</InputLabel>
              <NativeSelect
                value={source.value}
                onChange={handleSourceChange}
                inputProps={{
                  name: 'Character A',
                  id: 'age-native-helper',
                }}
              >
                <option aria-label="None" value="" />
                {characterNames.map((name) => {
                  return <option value={name}>{name}</option>
                })}
                
              </NativeSelect>
              <FormHelperText>the Bond "owner"</FormHelperText>
            </FormControl>
          {/* <CharacterNameAutoComplete label="who has the relation...?" value={source} onChange={handleSourceChange}/>

          <CharacterNameAutoComplete label="... with who?" value={target} onChange={handleTargetChange}/> */}

          {/* <TextField
            autoFocus
            margin="dense"
            id="source"
            label="source"
            fullWidth
            value={source}
            onChange={handleSourceChange}
          />
          <TextField
            margin="dense"
            id="target"
            label="short target"
            fullWidth
            value={target}
            onChange={handleTargetChange}
          /> */}


        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            CANCEL
          </Button>
          <Button variant="contained" onClick={handleSubmit} color="primary">
            <Add fontSize="small"/>
            ADD
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}

export default NewBondForm
